    private static void playGame(String gameWord){
        System.out.println(gameWord);
        int numTries = gameWord.length() + 3, numLettersLeft = gameWord.length();
        char[] gameWord_ary = gameWord.toCharArray(), guessWord_ary = new char[gameWord_ary.length];
        Arrays.fill(guessWord_ary, '-');
        char guessLetter;
        Scanner in = new Scanner(System.in);
        System.out.println("Let's start: ");
        while(numTries > 0){
            System.out.println("Number of tries left: " + numTries);
            for(char c : guessWord_ary)
                System.out.print(c);
            System.out.println();
            String guess;
            do {
                System.out.print("Guess a letter or a word: ");
                guess = in.next();
            } while(guess.length() == 0 || guess.length() > gameWord.length() || (guess.length() > 1 && guess.length() != gameWord.length()));
            if(guess.length() == gameWord.length()){
                for (int i = 0; i < guess.length(); i++) {
                    if (gameWord.charAt(i) == guess.charAt(i)) {
                        numLettersLeft--;
                    }
                }
            }
            else {
                guessLetter = guess.charAt(0);
                for (int i = 0; i < gameWord_ary.length; i++) {
                    if (gameWord_ary[i] == guessLetter) {
                        guessWord_ary[i] = guessLetter;
                        numLettersLeft--;
                    }
                }
            }
            if(numLettersLeft <= 0){
                System.out.println("*-----*-----*-----*-----*");
                System.out.println("Good work! The game word was: " + gameWord + ".");
                System.exit(1);
            }
            numTries--;
            System.out.println("*-----*-----*-----*-----*");
        }
        System.out.println("Game over! The game word was: " + gameWord + ". You had " + numLettersLeft + " letters left to guess.");
        System.exit(1);
    }
